import React, { useEffect, useState } from 'react';
import { fetchCommunities, fetchCommunityKeywords, fetchCommunityPagerank } from '../services/api';

function CommunityPage() {
  const [data, setData] = useState([]);
  const [pagerank, setPagerank] = useState({});
  const [keywordMap, setKeywordMap] = useState({});  // 커뮤니티별 키워드 상태 저장

  useEffect(() => {
    fetchCommunities().then(res => {
      setData(res.data);
    });

    fetchCommunityPagerank().then(res => {
      const prDict = {};
      res.data.forEach(item => {
        prDict[item.community_id] = item.pagerank;
      });
      setPagerank(prDict);
    });
  }, []);

  const handleKeywordClick = (cid) => {
    if (keywordMap[cid]) {
      // 이미 키워드가 로드되어 있으면 토글로 제거
      setKeywordMap(prev => {
        const newMap = { ...prev };
        delete newMap[cid];
        return newMap;
      });
    } else {
      fetchCommunityKeywords(cid).then(res => {
        setKeywordMap(prev => ({
          ...prev,
          [cid]: res.data.keywords
        }));
      });
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Community List</h2>
      <ul>
        {data.map((item, idx) => (
          <li key={idx} style={{ marginBottom: '10px', borderBottom: '1px solid #ddd', paddingBottom: '10px' }}>
            <strong>Community {item.community_id}</strong>: {item.size} members
            <br />
            Pagerank: {(pagerank[item.community_id] ?? 0).toFixed(5)}
            <br />
            <button onClick={() => handleKeywordClick(item.community_id)}>
              {keywordMap[item.community_id] ? 'Hide Keywords' : 'View Keywords'}
            </button>

            {keywordMap[item.community_id] && (
              <ul style={{ marginTop: '10px' }}>
                {keywordMap[item.community_id].map((kw, i) => <li key={i}>{kw}</li>)}
              </ul>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default CommunityPage;
