import React, { useEffect, useRef, useState } from 'react';
import * as d3 from 'd3';

function TragectoryPage() {
  const svgRef = useRef();
  const [allData, setAllData] = useState(null);
  const [coauthorStats, setCoauthorStats] = useState([]);
  const [searchKeyword, setSearchKeyword] = useState('');
  const [suggestions, setSuggestions] = useState([]);

  useEffect(() => {
    fetch('/coauthorship_graph_with_year.json')
      .then(res => res.json())
      .then(json => {
        const nodes = json.nodes.map(d => ({ ...d, name: d.id || '' }));
        const links = json.links.map(d => ({
          source: d.source,
          target: d.target,
          weight: d.weight,
          years: d.years
        }));
        setAllData({ nodes, links });
      });
  }, []);

  const handleInputChange = (e) => {
    const keyword = e.target.value;
    setSearchKeyword(keyword);
    if (!keyword || !allData) {
      setSuggestions([]);
      return;
    }
    const matched = allData.nodes
      .filter(n => n.name.toLowerCase().includes(keyword.toLowerCase()))
      .slice(0, 10);
    setSuggestions(matched);
  };

  const handleSearch = () => {
    if (!allData) return;
    const author = allData.nodes.find(n => n.name.toLowerCase() === searchKeyword.toLowerCase());
    if (!author) {
      alert('저자를 찾을 수 없습니다.');
      return;
    }
    computeTrajectory(author.name);
  };

  const computeTrajectory = (authorName) => {
    if (!allData) return;

    const yearlySum = {};
    allData.links.forEach(link => {
      const years = link.years?.split(',').map(y => parseInt(y.trim())) || [];
      if (link.source === authorName || link.target === authorName) {
        years.forEach(year => {
          yearlySum[year] = (yearlySum[year] || 0) + link.weight;
        });
      }
    });

    const data = Object.entries(yearlySum)
      .sort((a, b) => a[0] - b[0])
      .map(([year, sum]) => ({ year: +year, coauthorshipSum: sum }));

    setCoauthorStats(data);
    drawBarChart(data);
  };

  const drawBarChart = (data) => {
    const svg = d3.select(svgRef.current);
    svg.selectAll('*').remove();
    const width = 800;
    const height = 400;
    const margin = { top: 20, right: 30, bottom: 40, left: 60 };

    const x = d3.scaleBand()
      .domain(data.map(d => d.year))
      .range([margin.left, width - margin.right])
      .padding(0.1);

    const y = d3.scaleLinear()
      .domain([0, d3.max(data, d => d.coauthorshipSum)]).nice()
      .range([height - margin.bottom, margin.top]);

    svg.attr('viewBox', `0 0 ${width} ${height}`);

    svg.append('g')
      .selectAll('rect')
      .data(data)
      .join('rect')
      .attr('x', d => x(d.year))
      .attr('y', d => y(d.coauthorshipSum))
      .attr('height', d => y(0) - y(d.coauthorshipSum))
      .attr('width', x.bandwidth())
      .attr('fill', 'steelblue');

    svg.append('g')
      .attr('transform', `translate(0,${height - margin.bottom})`)
      .call(d3.axisBottom(x).tickFormat(d3.format('d')));

    svg.append('g')
      .attr('transform', `translate(${margin.left},0)`)
      .call(d3.axisLeft(y));
  };

  return (
    <div style={{ padding: '30px' }}>
      <h2>Author Coauthorship Yearly Sum</h2>
      <div style={{ marginBottom: '20px' }}>
        <input
          type="text"
          value={searchKeyword}
          onChange={handleInputChange}
          placeholder="저자 이름 검색"
          style={{ width: '300px', padding: '8px', marginRight: '10px' }}
        />
        <button onClick={handleSearch} style={{ padding: '8px 16px' }}>Search</button>
        {suggestions.length > 0 && (
          <ul style={{ listStyle: 'none', padding: '10px', backgroundColor: '#f0f0f0', position: 'absolute' }}>
            {suggestions.map(node => (
              <li key={node.id} onClick={() => { setSearchKeyword(node.name); computeTrajectory(node.name); }} style={{ cursor: 'pointer' }}>{node.name}</li>
            ))}
          </ul>
        )}
      </div>
      <svg ref={svgRef} width="800" height="400" />
    </div>
  );
}

export default TragectoryPage;
