import axios from 'axios';

const api = axios.create({
  baseURL: 'http://127.0.0.1:8000/api',
});

// trajectory (수정 반영)
export const fetchTrajectory = (author) => api.get(`/trajectory/${author}`);
export const fetchAllAuthors = () => api.get('/trajectory/all-authors');
export const fetchRecommendedAuthors = (top_n = 20) => api.get(`/trajectory/recommended-authors?top_n=${top_n}`);

// influence
export async function fetchInfluenceByAuthor() {
  return await fetch('/influence/author').then(res => res.json());
}

export async function fetchInfluenceByPaper() {
  return await fetch('/influence/paper').then(res => res.json());
}
