// CitationYearPage with PageRank-based top 10 highlighting
import React, { useEffect, useRef, useState } from 'react';
import * as d3 from 'd3';

function CitationYearPage() {
  const svgRef = useRef();
  const [yearlyEmergingKeywords, setYearlyEmergingKeywords] = useState({});
  const [allYearData, setAllYearData] = useState({});
  const [selectedYear, setSelectedYear] = useState('2015-2017');
  const [currentData, setCurrentData] = useState(null);
  const [selectedCluster, setSelectedCluster] = useState(null);
  const [selectedPaper, setSelectedPaper] = useState(null);
  const [searchKeyword, setSearchKeyword] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [citationLookup, setCitationLookup] = useState({});
  const [pagerankLookup, setPagerankLookup] = useState({});
  const [errorMessage, setErrorMessage] = useState('');
  const simulationRef = useRef(null);

  const availableYears = [
    { label: '2015-2017', file: 'cluster_2015_2017.json' },
    { label: '2016-2018', file: 'cluster_2016_2018.json' },
    { label: '2017-2019', file: 'cluster_2017_2019.json' },
    { label: '2018-2020', file: 'cluster_2018_2020.json' },
    { label: '2019-2021', file: 'cluster_2019_2021.json' },
    { label: '2020-2022', file: 'cluster_2020_2022.json' },
    { label: '2021-2023', file: 'cluster_2021_2023.json' },
    { label: '2022-2024', file: 'cluster_2022_2024.json' }
  ];

  useEffect(() => {
    fetch('/citation_graph_with_cluster_v3.json')
      .then(res => res.json())
      .then(json => {
        const map = {};
        json.nodes.forEach(n => {
          let authors = [];
          if (Array.isArray(n.authors)) authors = n.authors;
          else if (typeof n.authors === 'string') {
            try { authors = JSON.parse(n.authors); }
            catch { authors = n.authors.split(/,\s*/); }
          }
          map[n.id] = {
            authors,
            year: n.year,
            citationCount: n.citationCount ?? 0
          };
        });
        setCitationLookup(map);
      });

    fetch('/papers_by_pagerank.json')
      .then(res => res.json())
      .then(data => {
        const lookup = {};
        data.forEach(p => lookup[p.id] = p.pagerank);
        setPagerankLookup(lookup);
      });

    Promise.all(availableYears.map(year => 
      fetch(`/cluster_with_keywords_by_year/${year.file}`).then(res => res.json())
    ))
    .then(dataArray => {
      const yearDataMap = {};
      availableYears.forEach((year, idx) => {
        yearDataMap[year.label] = dataArray[idx];
      });
      setAllYearData(yearDataMap);
      setSelectedYear(availableYears[0].label);
      setCurrentData(yearDataMap[availableYears[0].label]);
    });
  }, []);

  useEffect(() => {
    if (!currentData || !pagerankLookup) return;

    const svg = d3.select(svgRef.current);
    svg.selectAll('*').remove();
    const width = 1000, height = 700;
    svg.attr('viewBox', [0, 0, width, height]);
    const svgGroup = svg.append('g');

    const nodes = [], links = [];
    const topPapersByCluster = {};

    Object.entries(currentData.cluster_details).forEach(([clusterId, papers]) => {
      const sorted = [...papers]
        .map(p => ({ ...p, pagerank: pagerankLookup[p.id] || 0 }))
        .sort((a, b) => b.pagerank - a.pagerank);
      topPapersByCluster[clusterId] = sorted.slice(0, 10).map(p => p.id);

      nodes.push({
        id: `cluster_${clusterId}`,
        name: `Cluster ${clusterId}`,
        type: 'cluster',
        clusterId,
      });

      sorted.forEach(p => {
        nodes.push({
          id: p.id,
          name: p.title,
          type: 'paper',
          clusterId,
          pagerank: p.pagerank || 0
        });
        links.push({ source: `cluster_${clusterId}`, target: p.id });
      });
    });

    const simulation = d3.forceSimulation(nodes)
      .force('link', d3.forceLink(links).id(d => d.id).distance(80))
      .force('charge', d3.forceManyBody().strength(-120))
      .force('center', d3.forceCenter(width / 2, height / 2))
      .force('collision', d3.forceCollide().radius(20));

    const link = svgGroup.append('g')
      .attr('stroke', '#aaa')
      .selectAll('line')
      .data(links)
      .join('line')
      .attr('stroke-width', 1);

    const color = d3.scaleOrdinal(d3.schemeCategory10);

    const node = svgGroup.append('g')
      .selectAll('circle')
      .data(nodes)
      .join('circle')
      .attr('r', d => d.type === 'cluster' ? 20 : (topPapersByCluster[d.clusterId]?.includes(d.id) ? 12 : 6))
      .attr('fill', d => d.type === 'cluster' ? '#444' : color(d.clusterId))
      .attr('stroke', d => topPapersByCluster[d.clusterId]?.includes(d.id) ? 'gold' : '#fff')
      .attr('stroke-width', d => topPapersByCluster[d.clusterId]?.includes(d.id) ? 2 : 1.2)
      .call(drag(simulation));

    simulation.on('tick', () => {
      link
        .attr('x1', d => d.source.x)
        .attr('y1', d => d.source.y)
        .attr('x2', d => d.target.x)
        .attr('y2', d => d.target.y);
      node
        .attr('cx', d => d.x)
        .attr('cy', d => d.y);
    });

    function drag(simulation) {
      return d3.drag()
        .on('start', (event, d) => {
          if (!event.active) simulation.alphaTarget(0.3).restart();
          d.fx = d.x; d.fy = d.y;
        })
        .on('drag', (event, d) => {
          d.fx = event.x; d.fy = event.y;
        })
        .on('end', (event, d) => {
          if (!event.active) simulation.alphaTarget(0);
          d.fx = null; d.fy = null;
        });
    }
  }, [currentData, pagerankLookup]);

  return (
    <div style={{ display: 'flex', flexDirection: 'column' }}>
      <h2 style={{ margin: '10px' }}>ðŸ“ˆ Citation Graph by Year</h2>
      <select value={selectedYear} onChange={(e) => {
        setSelectedYear(e.target.value);
        setCurrentData(allYearData[e.target.value]);
      }}>
        {availableYears.map(y => <option key={y.label}>{y.label}</option>)}
      </select>
      <svg ref={svgRef} width={1000} height={700} />
    </div>
  );
}

export default CitationYearPage;
