// 완성된 코드입니다: AuthorYearPage.js

import React, { useEffect, useRef, useState } from 'react';
import * as d3 from 'd3';

const availableWindows = [
  '2015_2017', '2016_2018', '2017_2019', '2018_2020',
  '2019_2021', '2020_2022', '2021_2023', '2022_2024'
];

const MAX_AUTHORS_PER_CLUSTER = 60;

function AuthorYearPage() {
  const svgRef = useRef();
  const [selectedWindow, setSelectedWindow] = useState(availableWindows[0]);
  const [authorData, setAuthorData] = useState({});
  const [selectedCluster, setSelectedCluster] = useState(null);
  const [searchKeyword, setSearchKeyword] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [highlightedNodeId, setHighlightedNodeId] = useState(null);
  const [highlightedClusterNodeId, setHighlightedClusterNodeId] = useState(null);
  const simulationRef = useRef(null);
  const [allPapers, setAllPapers] = useState([]);
  const [selectedAuthor, setSelectedAuthor] = useState(null);
  const [top5AuthorsPerCluster, setTop5AuthorsPerCluster] = useState({});

  useEffect(() => {
    fetch('/data/citation_graph_with_cluster_v3.json')
        .then(res => res.json())
        .then(data => setAllPapers(data.nodes || [])) // nodes 배열 사용
        .catch(err => console.error('논문 데이터 로딩 실패:', err));
    }, []);

    const getAuthorPapers = (authorName) => {
        return allPapers.filter(paper => {
            let authors = [];
            try {
            authors = JSON.parse(paper.authors);
            } catch {
            authors = paper.authors?.split(/,\s*/) || [];
            }
            return authors.includes(authorName);
        });
        };

  const safeSelector = (id) => {
    let cleanedId = id.replace(/[^a-zA-Z0-9_-]/g, '_');
    if (!/^[a-zA-Z_]/.test(cleanedId)) {
      cleanedId = '_' + cleanedId;
    }
    return cleanedId;
  };

  const zoomInToNode = (nodeId) => {
    const svg = d3.select(svgRef.current);
    const width = 1000, height = 700;
    const node = d3.select(`g#${safeSelector(nodeId)}`);
    const d = node.datum();
    if (!d || d.x == null || d.y == null) return;

    if (simulationRef.current) simulationRef.current.stop();

    const scale = 1.5;
    const transform = d3.zoomIdentity.translate(width / 2 - d.x * scale, height / 2 - d.y * scale).scale(scale);
    const zoom = d3.zoom().on('zoom', (event) => {
      svg.select('g').attr('transform', event.transform);
    });
    svg.call(zoom);
    svg.transition().duration(750).call(zoom.transform, transform);
  };

  useEffect(() => {
    setSelectedCluster(null);
    fetch(`/top60_authors_per_cluster/top60_authors_${selectedWindow}.json`)
      .then(res => res.json())
      .then(json => {
        setAuthorData(json.top5_authors_per_cluster || {});
      })
      .catch(() => setAuthorData({}));
  }, [selectedWindow]);

  const handleSearchInputChange = (e) => {
    const value = e.target.value;
    setSearchKeyword(value);
    if (!value || value.length < 2) {
      setSuggestions([]);
      return;
    }
    const results = [];
    Object.entries(authorData).forEach(([cid, authors]) => {
      authors.forEach(a => {
        if (a.author.toLowerCase().includes(value.toLowerCase())) {
          results.push({ ...a, clusterId: cid });
        }
      });
    });
    setSuggestions(results.slice(0, 10));
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      const matched = suggestions[0];
      if (matched) handleNodeSelect(matched);
    }
  };

  const handleNodeSelect = (nodeObj) => {
    const clusterId = nodeObj.clusterId || Object.entries(authorData).find(([cid, authors]) =>
      authors.some(a => a.author === nodeObj.author)
    )?.[0];

    if (!clusterId) return;

    const clusterNodeId = `cluster_${clusterId}`;
    const authorId = nodeObj.author || nodeObj.id;

    setSelectedCluster(clusterId);
    setHighlightedNodeId(authorId);
    setHighlightedClusterNodeId(clusterNodeId);
    
  };

  const handleSuggestionClick = handleNodeSelect;

  useEffect(() => {
    if (!authorData) return;

    const width = 1000;
    const height = 700;
    const svg = d3.select(svgRef.current);
    svg.selectAll('*').remove();
    svg.attr('viewBox', [0, 0, width, height]);

    const svgGroup = svg.append('g');
    const nodes = [];
    const links = [];

    Object.entries(authorData).forEach(([cid, authors]) => {
      const clusterId = `cluster_${cid}`;
      nodes.push({ id: clusterId, name: `C${cid}`, type: 'cluster', clusterId: cid });
      authors.slice(0, MAX_AUTHORS_PER_CLUSTER).forEach(author => {
        nodes.push({ id: author.author, name: author.author, type: 'author', clusterId: cid });
        links.push({ source: clusterId, target: author.author });
      });
    });

    const color = d3.scaleOrdinal(d3.schemeCategory10);

    const simulation = d3.forceSimulation(nodes)
      .force('link', d3.forceLink(links).id(d => d.id).distance(80))
      .force('charge', d3.forceManyBody().strength(d => d.type === 'cluster' ? -500 : -100))
      .force('center', d3.forceCenter(width / 2, height / 2))
      .force('collision', d3.forceCollide(d => d.type === 'cluster' ? 40 : 20))
      .alphaDecay(0.05);
    simulationRef.current = simulation;

    const zoom = d3.zoom().on('zoom', (event) => {
      svgGroup.attr('transform', event.transform);
    });
    svg.call(zoom).call(zoom.transform, d3.zoomIdentity);

    const link = svgGroup.append('g')
      .attr('stroke', '#aaa')
      .attr('stroke-opacity', 0.3)
      .selectAll('line')
      .data(links)
      .join('line')
      .attr('stroke-width', 1);

    const nodeGroup = svgGroup.append('g')
      .selectAll('g')
      .data(nodes)
      .join('g')
      .attr('id', d => safeSelector(d.id))
      .call(d3.drag()
        .on('start', event => {
          if (!event.active) simulation.alphaTarget(0.3).restart();
          event.subject.fx = event.subject.x;
          event.subject.fy = event.subject.y;
        })
        .on('drag', event => {
          event.subject.fx = event.x;
          event.subject.fy = event.y;
        })
        .on('end', event => {
          if (!event.active) simulation.alphaTarget(0);
          event.subject.fx = null;
          event.subject.fy = null;
        })
      )
      .on('click', (_, d) => {
        handleNodeSelect(d);
      });

    nodeGroup.append('circle')
      .attr('r', d => d.type === 'cluster' ? 25 : 8)
      .attr('fill', d => color(d.clusterId))
      .attr('stroke', d => {
        if (d.id === highlightedNodeId || d.id === highlightedClusterNodeId) return 'red';
        return '#fff';
      })
      .attr('stroke-width', d => (d.id === highlightedNodeId || d.id === highlightedClusterNodeId) ? 3 : 1.5)
      .style('cursor', 'pointer');

    nodeGroup.append('text')
      .text(d => d.name?.length > 20 ? d.name.slice(0, 20) + '…' : d.name)
      .attr('text-anchor', 'middle')
      .attr('y', d => d.type === 'cluster' ? 5 : -12)
      .style('font-size', d => d.type === 'cluster' ? '14px' : '9px')
      .style('font-weight', d => d.type === 'cluster' ? 'bold' : 'normal')
      .style('cursor', 'pointer');

    simulation.on('tick', () => {
      link
        .attr('x1', d => d.source.x)
        .attr('y1', d => d.source.y)
        .attr('x2', d => d.target.x)
        .attr('y2', d => d.target.y);
      nodeGroup.attr('transform', d => `translate(${d.x},${d.y})`);
    });
  }, [authorData]);

  useEffect(() => {
    const svg   = d3.select(svgRef.current);
    const nodes = svg.selectAll('circle');

    nodes
        .attr('stroke', d =>
        (d.id === highlightedNodeId || d.id === highlightedClusterNodeId) ? 'red' : '#fff')
        .attr('stroke-width', d =>
        (d.id === highlightedNodeId || d.id === highlightedClusterNodeId) ? 3 : 1.5)
        .attr('r', d =>
        d.type === 'cluster'
            ? 25
            : (d.id === highlightedNodeId ? 12 : 8));

    if (highlightedNodeId) zoomInToNode(highlightedNodeId);
  }, [highlightedNodeId, highlightedClusterNodeId]);  // ✅ 이때만 줌 수행

  return (
    <div style={{ display: 'flex', height: '100vh' }}>
      <div style={{ flex: 1, position: 'relative' }}>
        <svg ref={svgRef} width={1000} height={700} />
      </div>
      <div style={{ background: '#f9f9f9', border: '1px solid #ddd', borderRadius: 10, padding: 15, boxShadow: '0 2px 5px rgba(0,0,0,0.1)', width: 350, margin: 10 }}>
        {selectedAuthor && (
          <>
            <h4 style={{ fontSize: '20px', marginTop: 0 }}>{selectedAuthor}의 논문</h4>
            {isTop5 && (
              <p style={{ color: '#ff6600' }}>이 저자는 이 클러스터의 Top 5 협업 저자 중 한 명입니다.</p>
            )}
            <ul style={{ maxHeight: 300, overflowY: 'auto', paddingLeft: 15 }}>
              {getAuthorPapers(selectedAuthor).slice(0, 10).map((paper, i) => (
                <li key={i} style={{ marginBottom: '8px' }}>
                  <strong>{paper.name}</strong><br />
                  <small>{paper.year} · 인용수: {paper.citationCount}</small>
                </li>
              ))}
            </ul>
          </>
        )}
        {!selectedAuthor && selectedCluster && authorData[selectedCluster] && (
          <>
            <h4 style={{ marginTop: 0 }}>Top 5 협업 저자</h4>
            <ul>
              {authorData[selectedCluster].slice(0, 5).map((a, i) => (
                <li key={i}>{a.author} - 협업 횟수: {a.total_internal_collab_weight}</li>
              ))}
            </ul>
            <h4 style={{ marginTop: 20 }}>이 클러스터의 저자 목록</h4>
            <ol style={{ maxHeight: 300, overflowY: 'auto', paddingLeft: 20 }}>
              {authorData[selectedCluster].slice(0, MAX_AUTHORS_PER_CLUSTER).map((a, i) => (
                <li
                  key={i}
                  style={{
                    cursor: 'pointer',
                    padding: '4px 8px',
                    borderRadius: '4px',
                    backgroundColor: highlightedNodeId === a.author ? '#e0f0ff' : 'transparent'
                  }}
                  onMouseEnter={(e) => e.currentTarget.style.backgroundColor = '#e0f0ff'}
                  onMouseLeave={(e) => e.currentTarget.style.backgroundColor = highlightedNodeId === a.author ? '#e0f0ff' : 'transparent'}
                  onClick={() => handleNodeSelect({ author: a.author, clusterId: selectedCluster })}
                >
                  <strong style={{ color: highlightedNodeId === a.author ? 'red' : 'inherit' }}>{a.author}</strong>
                </li>
              ))}
            </ol>
          </>
        )}
        {!selectedCluster && (
          <p>클러스터 또는 저자를 클릭하면 상세 정보가 표시됩니다.</p>
        )}
      </div>
    </div>
  );
}

export default AuthorYearPage;
