import { Box, Typography, useTheme } from '@mui/material';
import PlaceIcon from '@mui/icons-material/Place';
import CallIcon from '@mui/icons-material';
import TrendingUpIcon from '@mui/icons-material/TrendingUp';

import { LabelItems } from '~/components/molecule/label';
import { useNavigationSelector } from '~/store/reducers/navigateSlice';
import ModalLabel from '~/components/molecule/modalLabel';
import {
    CafePlaceContainer,
    CongestionBox,
    CongestionItem,
} from './cafeDetailInfo.styled';
import { options, Status } from '~/types/radio';

interface CafePlaceInfoProps {
    address: string;
    phoneNumber: string;
    isCongestion: boolean;
    hasPlugCount: number;
    isCleanCount: number;
    viewerCount: number;
    seatVacantCount: number;
    seatTotalCount: number;
    cafeId: string;
}

// ‚úÖ Dummy data for different cafes - now deterministic
const cafeData = {
    'cafe-1': {
        basePattern: [10, 15, 20, 25, 30, 40, 50, 65, 75, 80, 85, 90, // 0-11Ïãú
            95, 90, 85, 80, 75, 70, 80, 85, 70, 60, 40, 25], // 12-23Ïãú
        recommendedHours: 'Ïò§Ï†Ñ 7-9Ïãú, Ïò§ÌõÑ 3-5Ïãú',
    },
    'cafe-2': {
        basePattern: [5, 10, 15, 20, 25, 30, 40, 50, 60, 70, 75, 80,
            85, 80, 70, 60, 50, 40, 30, 25, 20, 15, 10, 5],
        recommendedHours: 'Ïò§ÌõÑ 8-10Ïãú, Ïò§Ï†Ñ 6-8Ïãú',
    },
    'cafe-3': {
        basePattern: [20, 25, 30, 35, 40, 50, 60, 70, 80, 90, 95, 90,
            80, 70, 60, 50, 40, 30, 25, 20, 15, 10, 5, 0],
        recommendedHours: 'Ïò§Ï†Ñ 0-2Ïãú, Ïò§ÌõÑ 10-11Ïãú',
    },
    // Add more cafe data as needed
    'default': { // Fallback for unknown cafeIds
        basePattern: [10, 15, 20, 25, 30, 40, 50, 65, 75, 80, 85, 90, // 0-11Ïãú
            95, 90, 85, 80, 75, 70, 80, 85, 70, 60, 40, 25], // 12-23Ïãú
        recommendedHours: 'Ïò§Ï†Ñ 7-9Ïãú, Ïò§ÌõÑ 3-5Ïãú',
    }
};

// ‚úÖ Updated: ÏãúÍ∞ÑÎåÄÎ≥Ñ ÌòºÏû°ÎèÑ ÏòàÏ∏° (cafeIdÏóê Îî∞Îùº Îã§Î•¥Í≤å, but deterministically)
const generateHourlyPrediction = (cafeId: string) => {
    const currentHour = new Date().getHours();
    const predictions = [];

    const data = cafeData[cafeId] || cafeData['default'];
    const basePattern = data.basePattern;

    for (let i = 0; i < 24; i++) {
        // ‚úÖ Removed random variation: use basePattern directly
        let percentage = basePattern[i];

        let status: Status = Status.spare;
        if (percentage > 70) status = Status.busy;
        else if (percentage > 30) status = Status.average;

        predictions.push({
            hour: i,
            percentage: Math.round(percentage),
            status,
            isCurrent: i === currentHour
        });
    }

    return predictions;
};

const CafePlaceInfo = ({
                           address,
                           phoneNumber,
                           isCongestion,
                           hasPlugCount,
                           isCleanCount,
                           viewerCount,
                           seatVacantCount,
                           seatTotalCount,
                           cafeId,
                       }: CafePlaceInfoProps) => {
    const theme = useTheme();
    const grayColor = theme.palette.grey[100];
    const iconColor = theme.palette.grey[300];
    const mainColor = theme.palette.primary.main;

    const navigate = useNavigationSelector();
    // Pass cafeId to generateHourlyPrediction
    const hourlyPredictions = generateHourlyPrediction(cafeId);

    // Get recommended hours based on cafeId
    const recommendedVisitHours = cafeData[cafeId]?.recommendedHours || cafeData['default'].recommendedHours;


    // ‚úÖ ÌòºÏû°ÎèÑ ÌåêÎã® Í∏∞Ï§Ä: Ï¢åÏÑù Ï†êÏú†Ïú® Í∏∞Î∞ò
    let status: Status = Status.unknown;
    if (seatTotalCount > 0) {
        const occupiedRatio = (seatTotalCount - seatVacantCount) / seatTotalCount;
        if (occupiedRatio <= 0.3) status = Status.spare;
        else if (occupiedRatio <= 0.7) status = Status.average;
        else status = Status.busy;
    }

    const viewerOption = options[status];

    // ÌòºÏû°ÎèÑ ÏÉÅÌÉúÎ≥Ñ ÏÉâÏÉÅ Îß§Ìïë
    const getStatusColor = (status: Status) => {
        switch (status) {
            case Status.spare: return '#4CAF50'; // Ï¥àÎ°ù
            case Status.average: return '#FF9800'; // Ï£ºÌô©
            case Status.busy: return '#F44336'; // Îπ®Í∞ï
            default: return '#9E9E9E'; // ÌöåÏÉâ
        }
    };

    const getStatusText = (status: Status) => {
        switch (status) {
            case Status.spare: return 'Ïó¨Ïú†';
            case Status.average: return 'Î≥¥ÌÜµ';
            case Status.busy: return 'ÌòºÏû°';
            default: return 'ÏïåÏàòÏóÜÏùå';
        }
    };

    return (
        <CafePlaceContainer color={grayColor} icon={iconColor}>
            {/* ‚úÖ ÏãúÏ≤≠Ïûê Ïàò Î∞ïÏä§ */}
            <Box
                sx={{
                    backgroundColor: viewerOption.color2,
                    color: viewerOption.color,
                    padding: '8px 12px',
                    borderRadius: '10px',
                    marginTop: '12px',
                    display: 'flex',
                    justifyContent: 'center',
                    alignItems: 'center',
                    height: '40px',
                }}
            >
                <Typography variant="subtitle2" textAlign="center">
                    üëÄ ÌòÑÏû¨{' '}
                    <span style={{ color: '#ff4545', fontWeight: 'bold' }}>
            {viewerCount}
          </span>
