import React, { useEffect, useState } from 'react';
import { fetchCommunities, fetchCommunityKeywords, fetchCommunityPagerank } from '../services/api';

function CommunityPage() {
  const [data, setData] = useState([]);
  const [pagerank, setPagerank] = useState({});
  const [selectedKeywords, setSelectedKeywords] = useState([]);

  useEffect(() => {
    fetchCommunities().then(res => {
      setData(res.data);
    });

    fetchCommunityPagerank().then(res => {
      const prDict = {};
      res.data.forEach(item => {
        prDict[item.community_id] = item.pagerank;
      });
      setPagerank(prDict);
    });
  }, []);

  const handleKeywordClick = (cid) => {
    fetchCommunityKeywords(cid).then(res => {
      setSelectedKeywords(res.data.keywords);
    });
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Community List</h2>
      <ul>
        {data.map((item, idx) => (
          <li key={idx} style={{ marginBottom: '10px' }}>
            <strong>Community {item.community_id}</strong>: {item.size} members
            <br />
            Pagerank: {(pagerank[item.community_id] ?? 0).toFixed(5)}
            <br />
            <button onClick={() => handleKeywordClick(item.community_id)}>View Keywords</button>
          </li>
        ))}
      </ul>

      {selectedKeywords.length > 0 && (
        <div style={{ marginTop: '30px' }}>
          <h3>Top Keywords:</h3>
          <ul>
            {selectedKeywords.map((kw, i) => <li key={i}>{kw}</li>)}
          </ul>
        </div>
      )}
    </div>
  );
}

export default CommunityPage;
