import axios from 'axios';

const api = axios.create({
  baseURL: 'http://127.0.0.1:8000/',
});

// trajectory (수정 반영)
export const fetchTrajectory = (author) => api.get(`/trajectory/${author}`);
export const fetchAllAuthors = () => api.get('/trajectory/all-authors');
export const fetchRecommendedAuthors = (top_n = 20) => api.get(`/trajectory/recommended-authors?top_n=${top_n}`);

// network summary
export const fetchNetworkSummary = () => api.get('/network/summary');

// influence
export const fetchInfluence = () => api.get('/influence/top');

// community
export const fetchCommunities = () => api.get('/community/list');
export const fetchCommunityDetails = (communityId) => api.get(`/community/detail/${communityId}`);
