import React, { useEffect, useState } from 'react';

function SearchPage() {
  const [papers, setPapers] = useState([]);
  const [keyword, setKeyword] = useState('GPT');
  const [results, setResults] = useState([]);

  useEffect(() => {
    fetch('papers_by_pagerank.json')
      .then(res => res.json())
      .then(data => {
        setPapers(data);
        setResults(filterByKeyword(data, keyword));
      });
  }, []);

  const filterByKeyword = (data, keyword) => {
    return data
      .filter(p => p.title.toLowerCase().includes(keyword.toLowerCase()))
      .sort((a, b) => b.pagerank - a.pagerank)
      .slice(0, 10);
  };

  const handleSearch = () => {
    const filtered = filterByKeyword(papers, keyword);
    setResults(filtered);
  };

  return (
    <div style={{ padding: '20px', maxWidth: '800px', margin: '0 auto' }}>
      <h2>üîç Find the Most Influential Papers Related to Your Interests</h2>
      <div style={{ marginBottom: '20px', display: 'flex', gap: '10px' }}>
        <input
          type="text"
          value={keyword}
          onChange={(e) => setKeyword(e.target.value)}
          placeholder="Enter a keyword (e.g., GPT)"
          style={{ flex: 1, padding: '8px', fontSize: '16px' }}
        />
        <button onClick={handleSearch} style={{ padding: '8px 16px', fontSize: '16px' }}>
          Search
        </button>
      </div>
      <ul>
        {results.map((paper, idx) => (
          <li key={paper.id} style={{ marginBottom: '15px', lineHeight: 1.6 }}>
            <strong>{idx + 1}. {paper.title}</strong><br />
            <span>üìÖ Year: {paper.year}</span><br />
            <span>üë• Authors: {JSON.parse(paper.authors).join(', ')}</span><br />
            <span>üìà Citation Count: {paper.citationCount}</span><br />
            <span>‚≠ê PageRank Score: {paper.pagerank.toExponential(3)}</span>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default SearchPage;
