import axios from 'axios';

const api = axios.create({
  baseURL: 'http://127.0.0.1:8000/api',
});

// ê¸°ì¡´ API
export const fetchInfluence = () => api.get('/influence/top');
export const fetchCommunities = () => api.get('/community/list');
export const fetchTrajectory = (author) => api.get(`/trajectory/${author}`);
export const fetchNetworkSummary = () => api.get('/network/summary');

// ðŸ”¥ ì‹ ê·œ ì¶”ê°€ API
export const fetchCommunityKeywords = (communityId) => api.get(`/community/keywords/${communityId}`);
export const fetchCommunityPagerank = () => api.get(`/community/pagerank`);
