import React, { useEffect, useState } from 'react';
import * as d3 from 'd3';

function EmergingPage() {
  const [windows, setWindows] = useState([]);
  const [hoveredWindow, setHoveredWindow] = useState(null);
  const [selectedWindow, setSelectedWindow] = useState(null);

  useEffect(() => {
    fetch('analysis_data_final.json')
      .then(res => res.json())
      .then(data => {
        const filtered = data.filter(w => w.topics && w.topics.length > 0);
        setWindows(filtered);
      })
      .catch(err => console.error('Failed to load analysis data:', err));
  }, []);

  const width = 1000;
  const height = 160;

  const getTopKeywords = (window) => {
    const wordStats = {};
    const totalClusters = window.topics.length;

    window.topics.forEach(topic => {
      const seen = new Set();
      topic.keywords.forEach((word, index) => {
        if (!wordStats[word]) {
          wordStats[word] = { tf: 0, df: 0, firstRank: index };
        }
        wordStats[word].tf += 1;
        if (!seen.has(word)) {
          wordStats[word].df += 1;
          seen.add(word);
        }
      });
    });

    const scored = Object.entries(wordStats).map(([word, { tf, df, firstRank }]) => {
      const idf = Math.log((1 + totalClusters) / (1 + df));
      return { word, score: tf * idf * (1 / (1 + firstRank)) };
    });

    return scored.sort((a, b) => b.score - a.score).slice(0, 10).map(k => k.word);
  };

  return (
    <div style={{ padding: '30px', maxWidth: '1100px', margin: '0 auto' }}>
      <h2>ðŸ“ˆ Emerging Topics Timeline</h2>

      <svg width={width} height={height} style={{ marginBottom: '30px' }}>
        <line x1={60} y1={height / 2} x2={width - 60} y2={height / 2} stroke="#ccc" strokeWidth={2} />
        {windows.map((win, i) => {
          const xScale = d3.scaleLinear()
            .domain([windows[0].window_start, windows[windows.length - 1].window_end])
            .range([80, width - 80]);
          const x = xScale(win.window_start);
          const isBoundary = i > 0 && windows[i - 1].window_end !== win.window_start;

          return (
            <g key={i} transform={`translate(${x}, ${height / 2})`}>
              {isBoundary && <rect x={-5} y={-20} width={10} height={40} fill="#ccc" onClick={() => setSelectedWindow(win)} style={{ cursor: 'pointer' }} />}
              <circle
                r={6}
                fill={hoveredWindow === win ? '#007bff' : '#555'}
                onMouseEnter={() => setHoveredWindow(win)}
                onMouseLeave={() => setHoveredWindow(null)}
                onClick={() => setSelectedWindow(win)}
                style={{ cursor: 'pointer' }}
              />
              <text y={20} textAnchor="middle" style={{ fontSize: '12px', fill: '#333' }}>
                {win.window_start}-{win.window_end}
              </text>
            </g>
          );
        })}
      </svg>

      {hoveredWindow && (
        <div style={{ marginBottom: '30px' }}>
          <h4>ðŸ”¥ Top Keywords in {hoveredWindow.window_start}-{hoveredWindow.window_end}</h4>
          <div style={{ display: 'flex', flexWrap: 'wrap', gap: '10px' }}>
            {getTopKeywords(hoveredWindow).map((kw, i) => (
              <span key={i} style={{ background: '#ffe0b2', padding: '6px 10px', borderRadius: '6px', fontSize: '13px' }}>{kw}</span>
            ))}
          </div>
        </div>
      )}

      {selectedWindow && (
        <div style={{ background: '#fff', padding: '20px', borderRadius: '10px', boxShadow: '0 2px 8px rgba(0,0,0,0.1)' }}>
          <h3>{selectedWindow.window_start} - {selectedWindow.window_end}</h3>

          <h4>ðŸ”¥ Top Keywords</h4>
          <div style={{ display: 'flex', flexWrap: 'wrap', gap: '10px', marginBottom: '15px' }}>
            {getTopKeywords(selectedWindow).map((kw, i) => (
              <span key={i} style={{ background: '#ffe0b2', padding: '6px 10px', borderRadius: '6px', fontSize: '13px' }}>{kw}</span>
            ))}
          </div>

          {selectedWindow.emerging_keywords && (
            <>
              <h4 style={{ marginTop: '20px' }}>ðŸŒ± Emerging Keywords</h4>
              <div style={{ display: 'flex', flexWrap: 'wrap', gap: '10px' }}>
                {selectedWindow.emerging_keywords.map((kw, i) => (
                  <span key={i} style={{ background: '#e0f7fa', padding: '6px 10px', borderRadius: '6px', fontSize: '13px' }}>{kw}</span>
                ))}
              </div>
            </>
          )}

          <h4>ðŸ§  Topics</h4>
          <ul>
            {selectedWindow.topics.map((topic, i) => (
              <li key={i} style={{ marginBottom: '10px' }}>
                <strong>Cluster {topic.cluster_id}</strong> (size: {topic.size}, confidence: {topic.confidence.toFixed(2)})<br />
                <span style={{ fontSize: '14px', color: '#555' }}>{topic.keywords.join(', ')}</span>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default EmergingPage;
