import React, { useEffect, useState } from 'react';
import { fetchCommunities, fetchCommunityDetails } from '../services/api';

function CommunityPage() {
  const [communities, setCommunities] = useState([]);
  const [detailsMap, setDetailsMap] = useState({});

  useEffect(() => {
    fetchCommunities().then(res => {
      setCommunities(res.data);
    });
  }, []);

  const handleDetailClick = (cid) => {
    if (detailsMap[cid]) {
      setDetailsMap(prev => {
        const newMap = { ...prev };
        delete newMap[cid];
        return newMap;
      });
    } else {
      fetchCommunityDetails(cid).then(res => {
        setDetailsMap(prev => ({
          ...prev,
          [cid]: res.data
        }));
      });
    }
  };

  return (
    <div style={{ padding: '30px' }}>
      <h2 style={{ textAlign: 'center', marginBottom: '30px' }}>Community List</h2>

      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px', justifyContent: 'center' }}>
        {communities.map((item) => (
          <div key={item.community_id} style={{
            background: '#fff',
            border: '1px solid #ddd',
            borderRadius: '10px',
            boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
            padding: '20px',
            width: '300px'
          }}>
            <h3>Community {item.community_id}</h3>
            <p>Members: <strong>{item.size}</strong></p>
            <p>Pagerank: <strong>{item.community_pagerank.toFixed(5)}</strong></p>

            <button onClick={() => handleDetailClick(item.community_id)} style={{
              padding: '8px 15px',
              backgroundColor: '#007bff',
              color: '#fff',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
              marginBottom: '10px'
            }}>
              {detailsMap[item.community_id] ? 'Hide Details' : 'View Details'}
            </button>

            {detailsMap[item.community_id] && (
              <div style={{ marginTop: '15px' }}>
                <h4>Top Authors</h4>
                <ul style={{ paddingLeft: '20px' }}>
                  {detailsMap[item.community_id].top_authors.map((author, i) => (
                    <li key={i}>{author.author} (Intra-PR: {author.intra_pagerank.toFixed(5)})</li>
                  ))}
                </ul>

                <h4>Top Papers</h4>
                <ul style={{ paddingLeft: '20px' }}>
                  {detailsMap[item.community_id].top_papers.map((paper, i) => (
                    <li key={i}>
                      {paper.title} (Citations: {paper.citationCount})
                    </li>
                  ))}
                </ul>

                <h4>Keywords</h4>
                <div style={{ display: 'flex', flexWrap: 'wrap', gap: '8px' }}>
                  {detailsMap[item.community_id].keywords.map((kw, i) => (
                    <span key={i} style={{
                      backgroundColor: '#f0f0f0',
                      borderRadius: '20px',
                      padding: '5px 12px',
                      fontSize: '13px'
                    }}>
                      {kw}
                    </span>
                  ))}
                </div>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default CommunityPage;
