import React, { useEffect, useState } from 'react';
import { fetchCommunities, fetchCommunityDetails } from '../services/api';

function CommunityPage() {
  const [communities, setCommunities] = useState([]);
  const [detailsMap, setDetailsMap] = useState({});  // 커뮤니티별 상세정보 저장

  useEffect(() => {
    fetchCommunities().then(res => {
      setCommunities(res.data);
    });
  }, []);

  const handleDetailClick = (cid) => {
    if (detailsMap[cid]) {
      // 토글: 이미 로드되어 있으면 숨김
      setDetailsMap(prev => {
        const newMap = { ...prev };
        delete newMap[cid];
        return newMap;
      });
    } else {
      fetchCommunityDetails(cid).then(res => {
        setDetailsMap(prev => ({
          ...prev,
          [cid]: res.data
        }));
      });
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Community List</h2>
      <ul>
        {communities.map((item, idx) => (
          <li key={idx} style={{ marginBottom: '10px', borderBottom: '1px solid #ddd', paddingBottom: '10px' }}>
            <strong>Community {item.community_id}</strong>: {item.size} members
            <br />
            Pagerank: {item.community_pagerank.toFixed(5)}
            <br />
            <button onClick={() => handleDetailClick(item.community_id)}>
              {detailsMap[item.community_id] ? 'Hide Details' : 'View Details'}
            </button>

            {detailsMap[item.community_id] && (
              <div style={{ marginTop: '10px' }}>
                <h4>Top Authors</h4>
                <ul>
                  {detailsMap[item.community_id].top_authors.map((author, i) => (
                    <li key={i}>{author.author} (Intra-PR: {author.intra_pagerank.toFixed(5)})</li>
                  ))}
                </ul>

                <h4>Top Papers</h4>
                <ul>
                  {detailsMap[item.community_id].top_papers.map((paper, i) => (
                    <li key={i}>
                      {paper.title} (Citations: {paper.citationCount})
                    </li>
                  ))}
                </ul>

                <h4>Keywords</h4>
                <ul>
                  {detailsMap[item.community_id].keywords.map((kw, i) => <li key={i}>{kw}</li>)}
                </ul>
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default CommunityPage;
