import React, { useEffect, useState } from 'react';

function SearchPage() {
  const [papers, setPapers] = useState([]);
  const [keyword, setKeyword] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [results, setResults] = useState([]);

  useEffect(() => {
    fetch('papers_by_pagerank.json')
      .then(res => res.json())
      .then(data => {
        setPapers(data);
      })
      .catch(err => console.error('ÎÖºÎ¨∏ Îç∞Ïù¥ÌÑ∞ Î°úÎî© Ïã§Ìå®:', err));
  }, []);

  const filterPapers = (keyword) => {
    const lowerKeyword = keyword.toLowerCase();
    return papers
      .filter(p =>
        p.title.toLowerCase().includes(lowerKeyword) ||
        (p.abstract && p.abstract.toLowerCase().includes(lowerKeyword))
      )
      .sort((a, b) => b.pagerank - a.pagerank)
      .slice(0, 10);
  };

  const handleInputChange = (e) => {
    const input = e.target.value;
    setKeyword(input);

    if (!input) {
      setSuggestions([]);
      return;
    }

    const lower = input.toLowerCase();
    const matches = papers
      .filter(p =>
        p.title.toLowerCase().includes(lower) ||
        (p.abstract && p.abstract.toLowerCase().includes(lower))
      )
      .map(p => p.title)
      .slice(0, 10);

    setSuggestions(matches);
  };

  const handleSearch = (kw) => {
    const searchTerm = kw || keyword;
    if (!searchTerm) return;
    const filtered = filterPapers(searchTerm);
    setResults(filtered);
    setSuggestions([]);
    setKeyword(searchTerm);
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  return (
    <div style={{ padding: '20px', maxWidth: '800px', margin: '0 auto', position: 'relative' }}>
      <h2>üîç Find the Most Influential Papers Related to Your Interests</h2>
      <div style={{ marginBottom: '20px', display: 'flex', gap: '10px' }}>
        <input
          type="text"
          value={keyword}
          onChange={handleInputChange}
          onKeyDown={handleKeyDown}
          placeholder="Enter a keyword (e.g., GPT)"
          style={{ flex: 1, padding: '8px', fontSize: '16px' }}
        />
        <button
          onClick={() => handleSearch()}
          style={{ padding: '8px 16px', fontSize: '16px' }}
        >
          Search
        </button>
      </div>

      {suggestions.length > 0 && (
        <ul style={{
          position: 'absolute',
          top: '95px',
          left: '0',
          width: '100%',
          background: '#fff',
          border: '1px solid #ddd',
          borderRadius: '5px',
          maxHeight: '200px',
          overflowY: 'auto',
          zIndex: 1000,
          listStyle: 'none',
          padding: '0',
          margin: '0'
        }}>
          {suggestions.map((title, idx) => (
            <li
              key={idx}
              style={{
                padding: '8px 12px',
                borderBottom: '1px solid #eee',
                cursor: 'pointer',
              }}
              onClick={() => handleSearch(title)}
            >
              {title.length > 100 ? title.slice(0, 100) + "..." : title}
            </li>
          ))}
        </ul>
      )}

      {results.length > 0 && (
        <div style={{ marginTop: '30px' }}>
          <h3>üìÑ Search Results</h3>
          <ul>
            {results.map((paper, idx) => (
              <li key={paper.id} style={{ marginBottom: '15px', lineHeight: 1.6 }}>
                <strong>{idx + 1}. {paper.title}</strong><br />
                <span>üìÖ Year: {paper.year}</span><br />
                <span>üë• Authors: {JSON.parse(paper.authors).join(', ')}</span><br />
                <span>üìà Citations: {paper.citationCount}</span><br />
                <span>‚≠ê PageRank: {paper.pagerank.toExponential(3)}</span>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default SearchPage;
