import 'aframe';
import React, { useEffect, useState } from 'react';
import { ForceGraph2D } from 'react-force-graph';

function CitationPage() {
  const [allData, setAllData] = useState({ nodes: [], links: [] });
  const [visibleData, setVisibleData] = useState({ nodes: [], links: [] });
  const [searchKeyword, setSearchKeyword] = useState('');
  const [suggestions, setSuggestions] = useState([]);

  useEffect(() => {
    fetch('/citation_graph.json')
      .then(res => res.json())
      .then(data => {
        setAllData(data);
      });
  }, []);

  const handleInputChange = (e) => {
    const keyword = e.target.value;
    setSearchKeyword(keyword);

    if (keyword.length < 2) {
      setSuggestions([]);
      return;
    }

    const matched = allData.nodes
      .filter(n => n.name.toLowerCase().includes(keyword.toLowerCase()))
      .slice(0, 10);
    setSuggestions(matched);
  };

  const handleSuggestionClick = (node) => {
    const paperId = node.id;

    const relatedLinks = allData.links.filter(
      link => link.source === paperId || link.target === paperId
    );

    const relatedNodeIds = new Set([
      paperId,
      ...relatedLinks.map(link => link.source),
      ...relatedLinks.map(link => link.target)
    ]);

    const limitedNodeIds = Array.from(relatedNodeIds).slice(0, 30);
    const filteredNodes = allData.nodes.filter(n => limitedNodeIds.includes(n.id));
    const filteredLinks = relatedLinks.filter(
      link => limitedNodeIds.includes(link.source) && limitedNodeIds.includes(link.target)
    );

    setVisibleData({ nodes: filteredNodes, links: filteredLinks });
    setSuggestions([]);
    setSearchKeyword(node.name);
  };

  return (
    <div style={{ width: '100%', height: '100vh' }}>
      <div style={{ margin: '10px', position: 'relative' }}>
        <input
          type="text"
          value={searchKeyword}
          onChange={handleInputChange}
          placeholder="논문 제목으로 검색"
          style={{ width: '300px' }}
        />
        {suggestions.length > 0 && (
          <ul style={{
            position: 'absolute',
            background: '#fff',
            border: '1px solid #ddd',
            listStyle: 'none',
            padding: '5px',
            margin: 0,
            width: '300px',
            zIndex: 10
          }}>
            {suggestions.map(node => (
              <li key={node.id} style={{ padding: '5px', cursor: 'pointer' }}
                  onClick={() => handleSuggestionClick(node)}>
                {node.name}
              </li>
            ))}
          </ul>
        )}
      </div>

      <ForceGraph2D
        graphData={visibleData}
        nodeAutoColorBy="year"
        nodeLabel="name"
        linkDirectionalParticles={1}
        linkDirectionalParticleSpeed={d => 0.005}
        nodeCanvasObject={(node, ctx, globalScale) => {
          const label = node.name;
          const fontSize = 12 / globalScale;
          ctx.font = `${fontSize}px Sans-Serif`;
          ctx.fillStyle = 'black';
          ctx.textAlign = 'center';
          ctx.fillText(label, node.x, node.y + 10);
        }}
      />
    </div>
  );
}

export default CitationPage;
