import React, { useState } from 'react';
import { fetchTrajectory } from '../services/api';

function TrajectoryPage() {
  const [author, setAuthor] = useState('');
  const [data, setData] = useState([]);

  const recommendedAuthors = [
    "Rachel Bawden",
    "Benoît Sagot",
    "Maged S. Al-Shaibani",
    "Yassir Fathullah",
    "Jaehoon Kim"
  ];

  const handleSearch = () => {
    if (!author) return;
    fetchTrajectory(author).then(res => {
      setData(res.data);
    }).catch(err => {
      console.error("API error:", err);
      setData([]);
    });
  };

  const handleRecommendClick = (name) => {
    setAuthor(name);
    fetchTrajectory(name).then(res => {
      setData(res.data);
    }).catch(err => {
      console.error("API error:", err);
      setData([]);
    });
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Author Trajectory</h2>

      <div style={{ marginBottom: '10px' }}>
        <input
          value={author}
          onChange={e => setAuthor(e.target.value)}
          placeholder="Enter author name"
          style={{ marginRight: '10px', padding: '5px' }}
        />
        <button onClick={handleSearch}>Search</button>
      </div>

      <div style={{ marginBottom: '20px' }}>
        <h4>Recommended Authors:</h4>
        {recommendedAuthors.map(name => (
          <button
            key={name}
            onClick={() => handleRecommendClick(name)}
            style={{ marginRight: '10px', marginBottom: '10px', padding: '5px 10px' }}
          >
            {name}
          </button>
        ))}
      </div>

      <ul>
      {data.length === 0 ? (
        <li>No data. Please search an author.</li>
      ) : (
        data.map((item, idx) => (
          <li key={idx}>
            {item.year} - Degree: {(item.degree ?? 0).toFixed(3)}
          </li>
        ))
      )}
    </ul>

    </div>
  );
}

export default TrajectoryPage;
