import React, { useEffect, useState } from 'react';
import { fetchCommunities, fetchCommunityKeywords, fetchCommunityPagerank } from '../services/api';

function CommunityPage() {
  const [data, setData] = useState([]);
  const [selected, setSelected] = useState(null);
  const [keywords, setKeywords] = useState([]);
  const [pagerank, setPagerank] = useState({});

  useEffect(() => {
    fetchCommunities().then(res => {
      setData(res.data);
    });

    fetchCommunityPagerank().then(res => {
      const prDict = {};
      res.data.forEach(item => {
        prDict[item.community_id] = item.pagerank;
      });
      setPagerank(prDict);
    });
  }, []);

  const handleSelect = (commId) => {
    setSelected(commId);
    fetchCommunityKeywords(commId).then(res => {
      setKeywords(res.data.keywords);
    });
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Community List</h2>
      <ul>
        {data.map((item, idx) => (
          <li key={idx}>
            <button onClick={() => handleSelect(item.community_id)}>
              Community {item.community_id} â€” {item.size} members
              {pagerank[item.community_id] && ` | PR: ${pagerank[item.community_id].toFixed(4)}`}
            </button>
          </li>
        ))}
      </ul>

      {selected !== null && (
        <div style={{ marginTop: '20px' }}>
          <h3>Community {selected} Keywords:</h3>
          <ul>
            {keywords.map((word, i) => (
              <li key={i}>{word}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default CommunityPage;
