import React, { useEffect, useRef, useState } from 'react';
import * as d3 from 'd3';

function CitationPage() {
  const svgRef = useRef();
  const [allData, setAllData] = useState(null);
  const [visibleData, setVisibleData] = useState(null);
  const [selectedNode, setSelectedNode] = useState(null);

  useEffect(() => {
    fetch('/citation_graph_with_cluster_v2.json')
      .then(res => res.json())
      .then(json => {
        const nodes = json.nodes;
        const nodeById = new Map(nodes.map(n => [n.id, n]));
        const links = json.links.map(d => ({
          source: nodeById.get(d.source),
          target: nodeById.get(d.target),
          value: d.weight
        }));
        setAllData({ nodes, links, nodeById });
      });
  }, []);

  const handleNodeClick = (node) => {
    if (!allData) return;

    const citing = new Set();
    const citedBy = new Set();

    allData.links.forEach(link => {
      if (link.source.id === node.id) citing.add(link.target.id);
      if (link.target.id === node.id) citedBy.add(link.source.id);
    });

    const related = new Set([node.id, ...citing, ...citedBy]);
    const filteredNodes = allData.nodes.filter(n => related.has(n.id));
    const nodeMap = new Map(filteredNodes.map(n => [n.id, n]));

    const filteredLinks = allData.links.filter(
      l => related.has(l.source.id) && related.has(l.target.id)
    ).map(link => ({
      source: nodeMap.get(link.source.id),
      target: nodeMap.get(link.target.id),
      value: link.value
    }));

    setVisibleData({ nodes: filteredNodes, links: filteredLinks });
    setSelectedNode(node);
  };

  useEffect(() => {
    if (!visibleData) return;
    const svg = d3.select(svgRef.current);
    svg.selectAll('*').remove();

    const width = 1000, height = 700;
    svg.attr('viewBox', [0, 0, width, height]);
    const svgGroup = svg.append('g');

    const color = d3.scaleOrdinal(d3.schemeCategory10);

    const simulation = d3.forceSimulation(visibleData.nodes)
      .force('link', d3.forceLink(visibleData.links).id(d => d.id).distance(200))
      .force('charge', d3.forceManyBody().strength(-300))
      .force('center', d3.forceCenter(width / 2, height / 2))
      .force('collision', d3.forceCollide(30));

    const link = svgGroup.append('g')
      .attr('stroke', '#aaa')
      .attr('stroke-opacity', 0.5)
      .selectAll('line')
      .data(visibleData.links)
      .join('line')
      .attr('stroke-width', d => Math.sqrt(d.value));

    const nodeGroup = svgGroup.append('g')
      .selectAll('g')
      .data(visibleData.nodes)
      .join('g')
      .call(d3.drag()
        .on('start', (event, d) => {
          if (!event.active) simulation.alphaTarget(0.3).restart();
          d.fx = d.x; d.fy = d.y;
        })
        .on('drag', (event, d) => {
          d.fx = event.x; d.fy = event.y;
        })
        .on('end', (event, d) => {
          if (!event.active) simulation.alphaTarget(0);
          d.fx = null; d.fy = null;
        })
      )
      .on('click', (event, d) => handleNodeClick(d));

    nodeGroup.append('circle')
      .attr('r', d => selectedNode && d.id === selectedNode.id ? 15 : 10)
      .attr('fill', d => color(d.group))
      .attr('stroke', d => selectedNode && d.id === selectedNode.id ? 'red' : '#fff')
      .attr('stroke-width', d => selectedNode && d.id === selectedNode.id ? 3 : 1);

    nodeGroup.append('text')
      .text(d => d.name.length > 25 ? d.name.substring(0, 25) + '...' : d.name)
      .attr('y', -15)
      .attr('text-anchor', 'middle')
      .style('font-size', '10px');

    simulation.on('tick', () => {
      link
        .attr('x1', d => d.source.x)
        .attr('y1', d => d.source.y)
        .attr('x2', d => d.target.x)
        .attr('y2', d => d.target.y);

      nodeGroup.attr('transform', d => `translate(${d.x},${d.y})`);
    });

    const zoom = d3.zoom().on('zoom', (event) => {
      svgGroup.attr('transform', event.transform);
    });
    svg.call(zoom);
  }, [visibleData, selectedNode]);

  return (
    <div style={{ display: 'flex' }}>
      <svg ref={svgRef} width="1000" height="700" />
      <div style={{ width: '300px', marginLeft: '10px' }}>
        {selectedNode && (
          <div>
            <h3>{selectedNode.name}</h3>
            <p><strong>연도:</strong> {selectedNode.year}</p>
            <p><strong>인용 수:</strong> {selectedNode.citationCount}</p>
            <p><strong>클러스터:</strong> {selectedNode.group}</p>
            <p><strong>저자:</strong></p>
            <ul>
              {selectedNode.authors && JSON.parse(selectedNode.authors).map((author, idx) => (
                <li key={idx}>{author}</li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}

export default CitationPage;
