import React, { useEffect, useRef, useState } from 'react';
import * as d3 from 'd3';

function CitationPage() {
  const svgRef = useRef();
  const [allData, setAllData] = useState(null);
  const [visibleData, setVisibleData] = useState(null);
  const [searchKeyword, setSearchKeyword] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [selectedNode, setSelectedNode] = useState(null);

  useEffect(() => {
    fetch('/citation_graph_with_cluster.json')
      .then(res => res.json())
      .then(json => {
        const nodes = json.nodes;
        const nodeById = new Map(nodes.map(d => [d.id, d]));
        const links = json.links.map(d => ({
          source: nodeById.get(d.source),
          target: nodeById.get(d.target),
          value: d.weight
        }));
        setAllData({ nodes, links });
      });
  }, []);

  const handleInputChange = (e) => {
    const keyword = e.target.value;
    setSearchKeyword(keyword);

    if (keyword.length < 2 || !allData) {
      setSuggestions([]);
      return;
    }

    const matched = allData.nodes
      .filter(n => n.name.toLowerCase().includes(keyword.toLowerCase()))
      .slice(0, 10);
    setSuggestions(matched);
  };

  const handleSuggestionClick = (node) => {
    const paperId = node.id;

    // 1. 먼저 링크 전체로부터 그래프를 만든다 (BFS를 위해)
    const graph = buildGraph(allData.links);

    // 2. BFS 탐색 (거리 기반으로 정확히 15개 추출)
    const visited = new Set();
    const queue = [paperId];
    const bfsResult = [];

    while (queue.length > 0 && bfsResult.length < 30) {
        const current = queue.shift();
        if (!visited.has(current)) {
        visited.add(current);
        bfsResult.push(current);
        const neighbors = graph.get(current) || [];
        queue.push(...neighbors);
        }
    }

    // 3. 이 bfsResult가 진짜 거리 기반 15개
    const filteredNodes = allData.nodes.filter(n => bfsResult.includes(n.id));
    const nodeById = new Map(filteredNodes.map(d => [d.id, d]));
    const filteredLinks = allData.links.filter(
        link => bfsResult.includes(link.source.id) && bfsResult.includes(link.target.id)
    ).map(link => ({
        source: nodeById.get(link.source.id),
        target: nodeById.get(link.target.id),
        value: link.value
    }));

    setVisibleData({ nodes: filteredNodes, links: filteredLinks });
    setSuggestions([]);
    setSearchKeyword(node.name);
    setSelectedNode(null);
    };

  const buildGraph = (links) => {
    const graph = new Map();
    links.forEach(link => {
        if (!graph.has(link.source.id)) graph.set(link.source.id, []);
        if (!graph.has(link.target.id)) graph.set(link.target.id, []);
        graph.get(link.source.id).push(link.target.id);
        graph.get(link.target.id).push(link.source.id);
    });
    return graph;
    };

  useEffect(() => {
    if (!visibleData) return;

    const svg = d3.select(svgRef.current);
    svg.selectAll('*').remove();

    const width = 1000;
    const height = 700;
    svg.attr('viewBox', [0, 0, width, height]);

    const svgGroup = svg.append("g");

    const minYear = d3.min(visibleData.nodes, d => d.year);
    const maxYear = d3.max(visibleData.nodes, d => d.year);
    const color = d3.scaleSequential(d3.interpolateViridis).domain([minYear, maxYear]);

    const simulation = d3.forceSimulation(visibleData.nodes)
      .force('link', d3.forceLink(visibleData.links).id(d => d.id).distance(150))
      .force('charge', d3.forceManyBody().strength(-200))
      .force('center', d3.forceCenter(width / 2, height / 2))
      .force('collision', d3.forceCollide(30));

    const link = svgGroup.append('g')
      .attr('stroke', '#aaa')
      .attr('stroke-opacity', 0.3)
      .selectAll('line')
      .data(visibleData.links)
      .join('line')
      .attr('stroke-width', d => Math.sqrt(d.value));

    const nodeGroup = svgGroup.append('g')
      .selectAll('g')
      .data(visibleData.nodes)
      .join('g')
      .call(drag(simulation))
      .on("click", (event, d) => setSelectedNode(d));

    nodeGroup.append('circle')
      .attr('r', d => d.name === searchKeyword ? 15 : 10)
      .attr('fill', d => color(d.year))
      .attr('stroke', d => d.name === searchKeyword ? 'red' : '#fff')
      .attr('stroke-width', d => d.name === searchKeyword ? 3 : 1)
      .style('cursor', 'pointer');

    nodeGroup.append('text')
      .text(d => d.name.length > 25 ? d.name.substring(0, 25) + "..." : d.name)
      .attr('text-anchor', 'middle')
      .attr('y', d => -15)
      .style('font-size', '10px')
      .style('cursor', 'pointer');

    simulation.on('tick', () => {
      link
        .attr('x1', d => d.source.x)
        .attr('y1', d => d.source.y)
        .attr('x2', d => d.target.x)
        .attr('y2', d => d.target.y);

      nodeGroup
        .attr('transform', d => `translate(${d.x},${d.y})`);
    });

    const zoom = d3.zoom().on("zoom", (event) => {
      svgGroup.attr("transform", event.transform);
    });
    svg.call(zoom);

    function drag(simulation) {
      function dragstarted(event, d) {
        if (!event.active) simulation.alphaTarget(0.3).restart();
        d.fx = d.x;
        d.fy = d.y;
      }
      function dragged(event, d) {
        d.fx = event.x;
        d.fy = event.y;
      }
      function dragended(event, d) {
        if (!event.active) simulation.alphaTarget(0);
        d.fx = null;
        d.fy = null;
      }
      return d3.drag().on("start", dragstarted).on("drag", dragged).on("end", dragended);
    }
  }, [visibleData, searchKeyword]);

  return (
    <div style={{ display: 'flex', height: '100vh' }}>
      <div style={{ flex: 1, position: 'relative' }}>
        <div style={{ margin: '10px' }}>
          <input
            type="text"
            value={searchKeyword}
            onChange={handleInputChange}
            placeholder="논문 제목 검색"
            style={{ width: '300px', padding: '5px' }}
          />
          {suggestions.length > 0 && (
            <ul style={{
              position: 'absolute',
              background: '#fff',
              border: '1px solid #ddd',
              listStyle: 'none',
              padding: '5px',
              margin: 0,
              width: '300px',
              zIndex: 10,
              maxHeight: '200px',
              overflowY: 'auto'
            }}>
              {suggestions.map(node => (
                <li key={node.id} style={{ padding: '5px', cursor: 'pointer' }}
                    onClick={() => handleSuggestionClick(node)}>
                  {node.name}
                </li>
              ))}
            </ul>
          )}
        </div>
        <svg ref={svgRef} width="1000" height="700" />
      </div>

      <div style={{ width: '300px', borderLeft: '1px solid #ddd', padding: '10px' }}>
        {selectedNode ? (
          <>
            <h3>논문 상세정보</h3>
            <p><strong>제목:</strong> {selectedNode.name}</p>
            <p><strong>저자:</strong> {selectedNode.authors}</p>
            <p><strong>연도:</strong> {selectedNode.year}</p>
            <p><strong>Citations:</strong> {selectedNode.citationCount}</p>
            <p><strong>클러스터:</strong> {selectedNode.group}</p>
          </>
        ) : (
          <p>노드를 클릭하면 상세정보가 표시됩니다</p>
        )}
      </div>
    </div>
  );
}

export default CitationPage;
