import React, { useState, useEffect } from 'react';
import data from '../pages_by_pagerank.json';
import './SearchPage.css';

function SearchPage() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);

  const handleSearch = (e) => {
    const value = e.target.value;
    setQuery(value);

    const q = value.toLowerCase();

    const matched = data.filter(paper =>
      paper.name.toLowerCase().includes(q) ||
      (paper.abstract && paper.abstract.toLowerCase().includes(q))
    );

    const sorted = matched.sort((a, b) => b.pagerank - a.pagerank);
    setResults(sorted.slice(0, 20)); // 상위 20개만 표시
  };

  return (
    <div>
      <h2>Semantic Paper Search</h2>
      <input
        type="text"
        placeholder="Enter keywords (e.g., 'turkey restaurant')"
        value={query}
        onChange={handleSearch}
        className="search-input"
      />

      {query && (
        <>
          <h3>Top Results for: <em>{query}</em></h3>
          <div className="paper-list">
            {results.map(paper => (
              <div key={paper.id} className="paper-item">
                <h4>{paper.name}</h4>
                <p><strong>Year:</strong> {paper.year} | <strong>PageRank:</strong> {paper.pagerank.toFixed(5)}</p>
                <p>{paper.abstract?.slice(0, 200)}...</p>
              </div>
            ))}
          </div>
        </>
      )}
    </div>
  );
}

export default SearchPage;
