import React, { useEffect, useState } from 'react';
import axios from 'axios';

function cosineSimilarity(a, b) {
  const dot = a.reduce((sum, ai, i) => sum + ai * b[i], 0);
  const normA = Math.sqrt(a.reduce((sum, ai) => sum + ai * ai, 0));
  const normB = Math.sqrt(b.reduce((sum, bi) => sum + bi * bi, 0));
  return dot / (normA * normB + 1e-8);
}

function SearchPage() {
  const [papers, setPapers] = useState([]);
  const [pagerankPapers, setPagerankPapers] = useState([]);
  const [keyword, setKeyword] = useState('');
  const [semanticResults, setSemanticResults] = useState([]);
  const [pagerankResults, setPagerankResults] = useState([]);
  const [activeTab, setActiveTab] = useState('semantic');
  const [loading, setLoading] = useState(false);
  const [pagerankMap, setPagerankMap] = useState(new Map());


  useEffect(() => {
    fetch('citation_graph_with_cluster_v3_embeddings.json')
      .then(res => res.json())
      .then(data => {
        const parsed = data.nodes.map(p => ({
          ...p,
          embedding: Array.isArray(p.embedding) ? p.embedding : JSON.parse(p.embedding || '[]')
        }));
        setPapers(parsed);
      });

    fetch('papers_by_pagerank.json')
      .then(res => res.json())
      .then(data => {
        const map = new Map(data.map(p => [p.id, p.pagerank]));
        setPagerankPapers(data);
        setPagerankMap(map); // 👈 이 줄을 추가
      });
  }, []);

  const handleSearch = async () => {
    if (!keyword.trim()) return;

    // Semantic search
    setLoading(true);
    try {
      const res = await axios.post('http://localhost:8000/embed', {
        title: keyword,
        abstract: ''
      });
      const queryEmbedding = res.data.embedding;

      const results = papers
      .filter(p => p.embedding.length)
      .map(p => ({
        ...p,
        similarity: cosineSimilarity(queryEmbedding, p.embedding),
        pagerank: pagerankMap.get(p.id) ?? null // 👈 pagerank 추가
      }))
      .sort((a, b) => b.similarity - a.similarity)
      .slice(0, 10);

      setSemanticResults(results);
    } catch (e) {
      console.error('Semantic search failed:', e);
    }
    setLoading(false);

    // PageRank keyword match
    const lower = keyword.toLowerCase();
    const prResults = pagerankPapers
      .filter(p => p.title.toLowerCase().includes(lower) || (p.abstract && p.abstract.toLowerCase().includes(lower)))
      .sort((a, b) => b.pagerank - a.pagerank)
      .slice(0, 10);

    setPagerankResults(prResults);
  };

  const renderPaper = (paper, idx, isSemantic = false) => (
    <li key={paper.id || idx} style={{ marginBottom: '15px', lineHeight: 1.6 }}>
      <strong>{idx + 1}. {paper.title || paper.name}</strong><br />
      <span>📅 Year: {paper.year ?? 'N/A'}</span><br />
      <span>👥 Authors: {paper.authors ? JSON.parse(paper.authors).join(', ') : 'N/A'}</span><br />
      <span>📈 Citation Count: {paper.citationCount ?? 'N/A'}</span><br />
      <span>⭐ PageRank Score: {paper.pagerank?.toExponential(3) ?? 'N/A'}</span><br />
      {isSemantic && <span>🔗 Similarity: {paper.similarity.toFixed(4)}</span>}
    </li>
  );

  <div style={{ display: 'flex', gap: '10px', marginTop: '30px' }}>
  <button
    onClick={() => setActiveTab('semantic')}
    style={{
      padding: '8px 16px',
      borderRadius: '5px',
      border: '1px solid #007bff',
      backgroundColor: activeTab === 'semantic' ? '#007bff' : '#fff',
      color: activeTab === 'semantic' ? '#fff' : '#007bff',
      cursor: 'pointer'
    }}
  >
    🧠 Semantic Search
  </button>
  <button
    onClick={() => setActiveTab('pagerank')}
    style={{
      padding: '8px 16px',
      borderRadius: '5px',
      border: '1px solid #007bff',
      backgroundColor: activeTab === 'pagerank' ? '#007bff' : '#fff',
      color: activeTab === 'pagerank' ? '#fff' : '#007bff',
      cursor: 'pointer'
    }}
  >
    🔤 Keyword + PageRank
  </button>
</div>

}

export default SearchPage;
